export function reducer(state, action) {
    if (action.type === 'READ_CARDS') {
        return {
            cards: [action.payload],
        };
    }
    if (action.type === 'ADD_BOOK') {
        return {
            myCards: [
                ...state.myCards,
                state.books.filter((b) => b.id === action.id),
            ],
        };
    }

    return state;
}
